import {Link, useNavigate} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {login} from "../services/userService";
import {Field, Form, Formik} from "formik";
import "./Login.css"

export default function Login() {

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleLogin = async (values) => {
        let a = await dispatch(login(values))
        console.log('a', a.payload.data.message)


        if (a.payload.data.message.token === "User is not exist") {
            alert("dang nhap ko thanh cong")
            navigate("/")
        } else if (a.payload.data.message.token === "Password is wrong") {
            alert("sai mk")
            navigate("/")
        } else {
            await dispatch(login(values))
            navigate("/home")
        }

    }
    console.clear();

    const loginBtn = document.getElementById('login');
    const signupBtn = document.getElementById('signup');

    loginBtn.addEventListener('click', (e) => {
        let parent = e.target.parentNode.parentNode;
        Array.from(e.target.parentNode.parentNode.classList).find((element) => {
            if(element !== "slide-up") {
                parent.classList.add('slide-up')
            }else{
                signupBtn.parentNode.classList.add('slide-up')
                parent.classList.remove('slide-up')
            }
        });
    });

    signupBtn.addEventListener('click', (e) => {
        let parent = e.target.parentNode;
        Array.from(e.target.parentNode.classList).find((element) => {
            if(element !== "slide-up") {
                parent.classList.add('slide-up')
            }else{
                loginBtn.parentNode.parentNode.classList.add('slide-up')
                parent.classList.remove('slide-up')
            }
        });
    });


    return (
        <div className="nghia">
            <Formik initialValues={{username: '', password: ''}} onSubmit={(values) => {
                handleLogin(values).then()
            }}>
                <Form>
                    <div className="form-structor">
                        <div className="signup">
                            <h2 className="form-title" id="signup"><span>or</span>Sign up</h2>
                            <div className="form-holder">
                                <input type="text" className="input" placeholder="Name"/>
                                <input type="email" className="input" placeholder="Email"/>
                                <input type="password" className="input" placeholder="Password"/>
                            </div>
                            <button className="submit-btn">Sign up</button>
                        </div>
                        <div className="login slide-up">
                            <div className="center">
                                <h2 className="form-title" id="login"><span>or</span>Log in</h2>
                                <div className="form-holder">
                                    <input type="email" className="input" placeholder="Email"/>
                                    <input type="password" className="input" placeholder="Password"/>
                                </div>
                                <button className="submit-btn">Log in</button>
                            </div>
                        </div>
                    </div>

                </Form>
            </Formik>

        </div>

    )
}
