import { Link, useNavigate } from "react-router-dom";
import { Field, Form, Formik } from "formik";
import { useDispatch, useSelector } from "react-redux";
import { updateBlogs, getBlogs } from "../../services/blogService";

export default function EditBlog() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const user = useSelector((state) => {
        return state.user.user;
    });
    const userId = user.message.token["idUser"];

    // Lấy thông tin blog từ Redux store
    const blog = useSelector((state) => {
        return state.blog.currentBlog;
    });

    const handleUpdate = (values) => {
        dispatch(updateBlogs(blog.id, values)).then((res) => {
            dispatch(getBlogs()).then(() => {
                navigate("/home");
            });
        });
    };

    // Kiểm tra xem blog đã được tải từ Redux store hay chưa
    if (!blog) {
        return <div>Loading...</div>;
    }

    return (
        <div className={"row"}>
            <div className={"col-12"}>
                <h1>Edit Blog</h1>
                <Formik
                    initialValues={{
                        userId: blog.userId,
                        name: blog.name,
                        date: blog.date,
                        amount: blog.amount,
                        type: blog.type,
                        tradeType: blog.tradeType,
                    }}
                    onSubmit={(values) => {
                        handleUpdate(values);
                    }}
                >
                    <Form>
                        <div className="offset-3 col-6">
                            <div className="form-group">
                                <label htmlFor="exampleInputPassword1">Name</label>
                                <Field
                                    type="text"
                                    className={"form-control"}
                                    name={"name"}
                                    placeholder={"Name"}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputPassword1">Date</label>
                                <Field
                                    type="date"
                                    className={"form-control"}
                                    name={"date"}
                                    placeholder={"Date"}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputPassword1">Amount</label>
                                <Field
                                    type="text"
                                    className={"form-control"}
                                    name={"amount"}
                                    placeholder={"Amount"}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputPassword1">Type</label>
                                <Field
                                    type="text"
                                    className={"form-control"}
                                    name={"type"}
                                    placeholder={"Type"}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputPassword1">Trade Type</label>
                                <Field
                                    type="text"
                                    className={"form-control"}
                                    name={"tradeType"}
                                    placeholder={"tradeType"}
                                />
                            </div>

                            <button type="submit" className="btn btn-primary">
                                Update
                            </button>
                        </div>
                    </Form>
                </Formik>
            </div>
        </div>
    );
}
