import {Link, useNavigate} from "react-router-dom";
import {Field, Form, Formik} from "formik";
import {useDispatch, useSelector} from "react-redux";
import {addBlogs, getBlogs} from "../../services/blogService";

export default function AddBlog (){
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const user = useSelector(state => {
        return state.user.user
    })
    const userId = user.message.token["idUser"]
    const handleAdd = (values) =>{
        dispatch(addBlogs(values)).then((res)=>{
            dispatch(getBlogs()).then(()=>{
                navigate('/home')
            })

        })

    }
    return(
        <div className="widget-post" aria-labelledby="post-header-title">
            <div className="widget-post__header">
                <h2 className="widget-post__title" id="post-header-title">
                    <i className="fa fa-pencil" aria-hidden="true"></i>
                    Write Me
                </h2>
            </div>
            <form id="widget-form" className="widget-post__form" name="form" aria-label="post widget">
                <div className="widget-post__content">
                    <label htmlFor="post-content" className="sr-only">Share</label>
                    <textarea name="post" id="post-content" className="widget-post__textarea scroller"
                              placeholder="What's wrong with r00tme?"></textarea>
                </div>
                <div className="widget-post__options is--hidden" id="stock-options">
                </div>
                <div className="widget-post__actions post--actions">
                    <div className="post-actions__attachments">
                        <button type="button" className="btn post-actions__stock attachments--btn"
                                aria-controls="stock-options" aria-haspopup="true">
                            <i className="fa fa-usd" aria-hidden="true"></i>
                            stock
                        </button>
                        <button type="button" className="btn post-actions__upload attachments--btn">
                            <label htmlFor="upload-image" className="post-actions__label">
                                <i className="fa fa-upload" aria-hidden="true"></i>
                                upload image
                            </label>
                        </button>
                        <input type="file" id="upload-image" accept="image/*" multiple/>
                    </div>
                    <div className="post-actions__widget">
                        <button className="btn post-actions__publish">publish</button>
                    </div>
                </div>
            </form>
        </div>
    )
}
