import {Link, useNavigate} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {login} from "../services/userService";
import {Field, Form, Formik} from "formik";


export default function Login() {

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleLogin = async (values) => {
        let a = await dispatch(login(values))
        console.log('a', a.payload.data.message)


        if (a.payload.data.message.token === "User is not exist") {
            alert("dang nhap ko thanh cong")
            navigate("/")
        } else if (a.payload.data.message.token === "Password is wrong") {
            alert("sai mk")
            navigate("/")
        } else {
            await dispatch(login(values))
            navigate("/home")
        }

    }


    return (

            <Formik initialValues={{username: '', password: ''}} onSubmit={(values) => {
                console.log(values)
                handleLogin(values).then()
            }}>
                <Form>
                    <div className="bg-white dark:bg-gray-900">
                        <div className="flex justify-center h-screen">
                            <div className="hidden bg-cover lg:block lg:w-2/3" style={{backgroundImage: 'url(https://images.unsplash.com/photo-1616763355603-9755a640a287?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80)'}}>
                                <div className="flex items-center h-full px-20 bg-gray-900 bg-opacity-40">
                                    <div>
                                        <h2 className="text-4xl font-bold text-white">Facebook</h2>
                                        <p className="max-w-xl mt-3 text-gray-300">Chào mừng mọi người đến với Facebook phiên bản fake</p>
                                    </div>
                                </div>
                            </div>
                            <div className="flex items-center w-full max-w-md px-6 mx-auto lg:w-2/6">
                                <div className="flex-1">
                                    <div className="text-center">
                                        <h2 className="text-4xl font-bold text-center text-gray-700 dark:text-white">Facebook</h2>
                                        <p className="mt-3 text-gray-500 dark:text-gray-300">Sign in to access your account</p>
                                    </div>
                                    <div className="mt-8">

                                            <div>
                                                <label htmlFor="text" className="block mb-2 text-sm text-gray-600 dark:text-gray-200">Username</label>
                                                <Field type="text" name="username" id="email" placeholder="example@example.com" className="block w-full px-4 py-2 mt-2 text-gray-700 placeholder-gray-400 bg-white border border-gray-200 rounded-md dark:placeholder-gray-600 dark:bg-gray-900 dark:text-gray-300 dark:border-gray-700 focus:border-blue-400 dark:focus:border-blue-400 focus:ring-blue-400 focus:outline-none focus:ring focus:ring-opacity-40" />
                                            </div>
                                            <div className="mt-6">
                                                <div className="flex justify-between mb-2">
                                                    <label htmlFor="password" className="text-sm text-gray-600 dark:text-gray-200">Password</label>
                                                    <a href="#" className="text-sm text-gray-400 focus:text-blue-500 hover:text-blue-500 hover:underline">Forgot password?</a>
                                                </div>
                                                <Field type="password" name="password" id="password" placeholder="Your Password" className="block w-full px-4 py-2 mt-2 text-gray-700 placeholder-gray-400 bg-white border border-gray-200 rounded-md dark:placeholder-gray-600 dark:bg-gray-900 dark:text-gray-300 dark:border-gray-700 focus:border-blue-400 dark:focus:border-blue-400 focus:ring-blue-400 focus:outline-none focus:ring focus:ring-opacity-40" />
                                            </div>
                                            <div className="mt-6">
                                                <button className="w-full px-4 py-2 tracking-wide text-white transition-colors duration-200 transform bg-blue-500 rounded-md hover:bg-blue-400 focus:outline-none focus:bg-blue-400 focus:ring focus:ring-blue-300 focus:ring-opacity-50">
                                                    Sign in
                                                </button>
                                            </div>

                                        <p className="mt-6 text-sm text-center text-gray-400">Don't have an account yet? <a href="#" className="text-blue-500 focus:outline-none focus:underline hover:underline">Sign up</a>.</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </Form>
            </Formik>
    )
}
