import {useDispatch, useSelector} from "react-redux";
import {useEffect, useState} from "react";
import {deleteBlogs, getBlogs} from "../../services/blogService";

import {Link, useNavigate} from "react-router-dom";
import {login} from "../../services/userService";

export default function ListBlog (){
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const [us, setUs] = useState()
    // const blogs = useSelector((state) => state.blog.blogs); // Lấy danh sách blog từ Redux store
    const blogs = useSelector((state) => {
        console.log(state.blogs.blogs)
        return state.blogs.blogs
    })
    let a = JSON.parse(localStorage.getItem('user'))
    useEffect(() =>{
        dispatch(getBlogs())
    }, [])
    const handleDelete =  ( (id)=>{
        dispatch(deleteBlogs(id)).then(()=>{
            dispatch(getBlogs())
            navigate("/home")
        })
    })
    useEffect(()=>{
        setUs()
    },[])
    // console.log(blogs[1].user.username)

    // console.log(blogs[0].user.username)
    console.log(blogs[0].user.username)
    return(
        <div>
        {blogs.map((item,key) =>(
                <div style={{marginTop:'100px', marginLeft:30 }} role="status" className=" w-full h-full p-4 border border-gray-200 rounded shadow md:p-6 dark:border-gray-700">
                    <div className="flex items-center justify-center  mb-4">
                        <img src={item.image} width={700} />
                    </div>
                    <div key={key} >
                        <div ><h1>{item.tittle}</h1></div>
                        <div style={{marginLeft:60}} ><h5>{item.content}</h5></div>
                        {/*<div >{item.user.name}</div>*/}

                    </div>
                    <h1>{item.user.username}</h1>
                    <div className="flex items-center mt-4 space-x-3">
                        <img style={{borderRadius: "50%", width: "60px", height: "60px"}} src={item.image} alt=""/>
                        {/*<div>*/}
                        {/*    <div  >{item.user.username}</div>*/}
                        {/*    */}
                        {/*    <div  >{item.date}</div>*/}
                        {/*</div>*/}
                    </div>
                </div>
            ))
        }
        </div>


    )
}
