import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {deleteBlogs, getBlogs} from "../../services/blogService";

import {Link, useNavigate} from "react-router-dom";
import {login} from "../../services/userService";

export default function ListBlog (){
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const blogs = useSelector(state => {
        return state.blogs.blogs
    })
    useEffect(() =>{
        dispatch(getBlogs())
    }, [])
    const handleDelete =  ( (id)=>{
      dispatch(deleteBlogs(id)).then(()=>{
            dispatch(getBlogs())
           navigate("/home")
        })
    })


    return(
                    <div style={{marginTop:'100px'}} role="status" className="max-w-sm p-4 border border-gray-200 rounded shadow md:p-6 dark:border-gray-700">
                        <div className="flex items-center justify-center h-48 mb-4 bg-gray-300 rounded dark:bg-gray-700">

                            <svg className="w-10 h-10 text-gray-200 dark:text-gray-600" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 16 20">
                                <path d="M14.066 0H7v5a2 2 0 0 1-2 2H0v11a1.97 1.97 0 0 0 1.934 2h12.132A1.97 1.97 0 0 0 16 18V2a1.97 1.97 0 0 0-1.934-2ZM10.5 6a1.5 1.5 0 1 1 0 2.999A1.5 1.5 0 0 1 10.5 6Zm2.221 10.515a1 1 0 0 1-.858.485h-8a1 1 0 0 1-.9-1.43L5.6 10.039a.978.978 0 0 1 .936-.57 1 1 0 0 1 .9.632l1.181 2.981.541-1a.945.945 0 0 1 .883-.522 1 1 0 0 1 .879.529l1.832 3.438a1 1 0 0 1-.031.988Z" />
                                <path d="M5 5V.13a2.96 2.96 0 0 0-1.293.749L.879 3.707A2.98 2.98 0 0 0 .13 5H5Z" />
                            </svg>
                        </div>

                        {
                            blogs.map((item,key) =>(
                                <div key={key} >
                                    <div className="h-2 bg-gray-200 rounded-full dark:bg-gray-700 mb-2.5">{item.title}</div>
                                    <div className="h-2 bg-gray-200 rounded-full dark:bg-gray-700 mb-2.5" />
                                    <div className="h-2 bg-gray-200 rounded-full dark:bg-gray-700 mb-2.5" />
                                    <div className="h-2 bg-gray-200 rounded-full dark:bg-gray-700 mb-2.5" />
                                    <div className="h-2 bg-gray-200 rounded-full dark:bg-gray-700" />
                                </div>
                            ))
                        }



                        <div className="flex items-center mt-4 space-x-3">
                            <svg className="w-10 h-10 text-gray-200 dark:text-gray-700" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
                                <path d="M10 0a10 10 0 1 0 10 10A10.011 10.011 0 0 0 10 0Zm0 5a3 3 0 1 1 0 6 3 3 0 0 1 0-6Zm0 13a8.949 8.949 0 0 1-4.951-1.488A3.987 3.987 0 0 1 9 13h2a3.987 3.987 0 0 1 3.951 3.512A8.949 8.949 0 0 1 10 18Z" />
                            </svg>
                            <div>
                                <div className="h-2.5 bg-gray-200 rounded-full dark:bg-gray-700 w-32 mb-2" />
                                <div className="w-48 h-2 bg-gray-200 rounded-full dark:bg-gray-700" />
                            </div>
                        </div>

                    </div>

    )
}
